import java.io.*;class Main{public static void main(String[]g)throws Exception{BufferedReader B=new BufferedReader(new InputStreamReader(System.in));int m[][]=new int[16][16],a[]=new int[3],i,j,x,y,s,d;for(String t;(t=B.readLine())!=null;){g=t.split(",");for(i=0;i<3;)a[i]=Integer.parseInt(g[i++]);x=a[0]+3;y=a[1]+3;s=a[2];d=s>2?2:1;for(i=x-d;i<=x+d;i++)for(j=y-d;j<=y+d;j++){if(s!=2){if(x-d<i&&i<x+d&&y-d<j&&j<y+d)m[i][j]++;else if(i==x||j==y)m[i][j]++;}else m[i][j]++;}}s=d=0;for(i=3;i<13;i++)for(j=3;j<13;j++)if(m[i][j]<1)s++;else d=m[i][j]>d?m[i][j]:d;System.out.println(s+"\n"+d);}}
