import java.util.*;
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int h = sc.nextInt();
		int w = sc.nextInt();
		int p = 0;//????????????????????????
		char weather[][] = new char[h][];//??\?????¨
		int ans[][] = new int[h][w];//????????¨
		for(int i = 0; i < h; i++){//????????????????????\???
			weather[i] = sc.next().toCharArray();
		}
		
		for(int i = 0; i < h; i++){//????????°?????????
			int cloud = -1;//???????????????????´?
			for(int k = 0; k < w; k++){
				if(weather[i][k] == 'c'){//????????£??????
					cloud = k;
					ans[i][k] = 0;
				}else if(cloud == -1 && weather[i][k] == '.'){//"."??§???????????´??????????????????
					ans[i][k] = -1;
				}else{//"."??§?????????????????????
					int d = k - cloud;//?????¨????????¢
					ans[i][k] = d;
				}
			}
		}
		for(int i = 0; i < h; i++){
			p = 0;
			for(int k = 0; k < w; k++){
				if(p == w-1){
					System.out.println(ans[i][k]);
				}else{
					System.out.print(ans[i][k]);
					System.out.print(" ");
					p++;
				}
			}
		}
		
	}

}