import java.util.*;class Main{public static void main(String[]z){int x,y,n=10,i,j,m,r=0,u=0;int[][]a=new int[n][n];for(Scanner s=new Scanner(System.in);s.hasNext();){String[]p=s.nextLine().split(",");x=new Integer(p[0]);y=new Integer(p[1]);m=new Integer(p[2]);for(i=u=0;i<n;i++)for(j=0;j<n;u+=a[i][j]<1?1:0,j++)r=Math.max(r,a[i][j]+=(m<2&&Math.abs(i-x)+Math.abs(j-y)<2)||(m>2&&Math.abs(i-x)+Math.abs(j-y)<3)||(m==2&&x-2<i&&i<x+2&&y-2<j&&j<y+2)?1:0);}System.out.println(u+"\n"+r);}}