import java.util.*;public class Main{public static void main(String[] args){Scanner s=new Scanner(System.in);String a,d=new String(),b=new String();int x=0,y=0,i=0;Hashtable<String,Integer> h=new Hashtable<String,Integer>();while(s.hasNext()){a=s.next();if(h.containsKey(a)){h.put(a,h.get(a)+1);}else{h.put(a,1);}}for(int o:h.values()){y++;if(x<o){x=o;i=y;}}for(String o:h.keySet()){i--;if(b.length()<o.length())b=o;if(i==0)d=o;}System.out.println(d+" "+b);}}