import java.util.*;class Main{public static void main(String[]z){Map<String,Map<Integer,Long>>m=new TreeMap<String,Map<Integer,Long>>();String k;for(Scanner s=new Scanner(System.in);s.hasNext();m.get(k).put(s.nextInt(),0L))if(!m.containsKey(k=s.next()))m.put(k,new TreeMap<Integer,Long>());k="";for(String x:m.keySet()){k+=x+"\n";for(int i:m.get(x).keySet())k+=i+" ";k=k.replaceAll(" $","\n");}System.out.print(k);}}