import java.util.*;class Main{public static void main(String[]z){int b=1;Map<Integer,Integer>m=new TreeMap<Integer,Integer>();for(Scanner s=new Scanner(System.in);s.hasNext();){String x=s.nextLine();if(x.isEmpty()){b=0;x=s.nextLine();}int y=new Integer(x.split(",")[0]);if(b>0)m.put(y,m.containsKey(y)?m.get(y)-1:-1);else if(m.containsKey(y))m.put(y,Math.abs(m.get(y))+1);}for(int y:m.keySet())if(m.get(y)>0)System.out.println(y+" "+m.get(y));}}