import java.util.*;class Main{public static void main(String[]z){for(Scanner s=new Scanner(System.in); s.hasNext();){int m=s.nextInt(),x=s.nextInt(),c=0,k,sum,i,j,t,p;long w,y,a,r,b,v;for(w=(1L<<m)-1;(w&~((1L<<10)-1))==0;){int[]l=new int[m],o;y=w;for(j=i=0;i++<10;y>>>=1)if((y&1)>0)l[j++]=i-1;a=w&-w;r=w+a;b=r&-r;v=((b/a)>>1)-1;w=r|v;o=new int[m+1];for(i=1;i<=m;o[i]=i++);for(k=0;k<m;o[k]--,c+=sum==x?1:0){t=l[k];l[k]=l[p=k%2<1?0:o[k]];l[p]=t;for(k=1;o[k]<1;o[k]=k++);for(sum=i=0;i++<m;)sum+=i*l[i-1];}}System.out.println(c);}}}