import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);int n=s.nextInt(),m=s.nextInt(),a,b;int[][]p=new int[n][n];for(;m-->0;){z=s.next().split(",");a=new Integer(z[0])-1;b=new Integer(z[1])-1;p[a][b]=new Integer(z[2]);p[b][a]=new Integer(z[3]);}z=s.next().split(",");System.out.println(new Integer(z[2])-new Integer(z[3])-f(a=new Integer(z[0])-1,b=new Integer(z[1])-1,p,n)-f(b,a,p,n));}static int f(int a,int b,int[][]g,int n){LinkedList<int[]> q=new LinkedList<int[]>();q.add(new int[]{a,0});int c=0,i;for(int[]v=new int[n];!q.isEmpty();){int[]p=q.poll();if(p[0]==b){c=p[1];break;}v[p[0]]=1;for(i=0;i<n;++i)if(v[i]<1&&p[0]!=i&&g[p[0]][i]>0)q.add(new int[]{i,p[1]+g[p[0]][i]});Collections.sort(q,new Comparator<int[]>(){public int compare(int[]x,int[]y){return x[1]-y[1];}});}return c;}}