import java.util.*;import static java.lang.Integer.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);int n=s.nextInt(),m=s.nextInt(),a,b,h=MAX_VALUE;int[][] p=new int[n][n];for(;m-->0;){z=s.next().split(",");a=valueOf(z[0])-1;b=valueOf(z[1])-1;p[a][b]=valueOf(z[2]);p[b][a]=valueOf(z[3]);}z=s.next().split(",");v=new int[n];System.out.println(valueOf(z[2])-valueOf(z[3])-f(a=valueOf(z[0])-1,b=valueOf(z[1])-1,p,n,h)-f(b,a,p,n,h));}static int f(int a,int b,int[][]g,int n,int m){if(a==b)return 0;int c=MAX_VALUE,i=0;for(;i<n;++i)if(v[i]<1&&g[a][i]>0){v[i]=1;c=Math.min(c,f(i,b,g,n,c)+g[a][b]);v[i]=0;if(c>m)return MAX_VALUE;}return c;}static int[]v;}