import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);int n=s.nextInt()+1,m=s.nextInt(),i=1,j,t;int[] p=new int[n*n];Arrays.fill(p,9999999);for(;i<n;++i)p[i*n+i]=0;for(;m-->0;){z=s.next().split(",");i=new Integer(z[0]);j=new Integer(z[1]);p[i*n+j]=new Integer(z[2]);p[j*n+i]=new Integer(z[3]);}for(t=1;t<n;++t)for(i=1;i<n;++i)for(j=1;j<n;++j)if(p[i*n+j]>(m=p[i*n+t]+p[t*n+j]))p[i*n+j]=m;z=s.next().split(",");i=new Integer(z[0]);j=new Integer(z[1]);System.out.println(new Integer(z[2])-new Integer(z[3])-p[i*n+j]-p[j*n+i]);}}