import java.util.*;import static java.lang.Integer.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);int n=s.nextInt(),m=s.nextInt(),a,b;int[][] p=new int[n][n];for(;m-->0;){z=s.next().split(",");a=valueOf(z[0])-1;b=valueOf(z[1])-1;p[a][b]=valueOf(z[2]);p[b][a]=valueOf(z[3]);}z=s.next().split(",");System.out.println(valueOf(z[2])-valueOf(z[3])-f(a=valueOf(z[0])-1,b=valueOf(z[1])-1,p,n)-f(b,a,p,n));}static int f(int a,int b,int[][]g,int n){LinkedList<int[]>q=new LinkedList<int[]>();int[]v={a,0};q.add(v);int c=MAX_VALUE,i,t,k,l;for(v=new int[n];!q.isEmpty();){k=q.peek()[0];l=q.poll()[1];if(k==b&&c>l){c=l;continue;}v[k]=1;for(i=0;i<n;++i)if(v[i]<1&&k!=i&&(t=g[k][i])>0)q.add(new int[]{i,l+t});Collections.sort(q,new Comparator<int[]>(){public int compare(int[]x,int[]y){return x[1]-y[1];}});}return c;}}