import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);String y;for(int n,i;(n=s.nextInt())>0;){List<String>l=new ArrayList<String>();Map<String,Integer>m=new HashMap<String,Integer>();for(i=n;i-->0;){l.add(y=s.next());m.put(y,s.nextInt()*3+s.nextInt()*0+s.nextInt());}List<Integer>c=new ArrayList<Integer>(m.values());Collections.sort(c);for(;n-->0;)for(String k:l)if(c.get(n).equals(m.get(k))){System.out.println(k+","+c.get(n));m.remove(k);}System.out.println();}}}