import java.util.*;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int cnt = 0, space, work;
		boolean flag;
		char[] target = sc.next().toCharArray();
		
		for(int i=0; i<n; i++){
			char[] old = sc.next().toCharArray();
			flag = true;
			for(int j=0; j<old.length; j++){
				if(target[0]==old[j]){//??????????????????????????£??????
					
					for(int k=j+1; k<old.length; k++){
						if(target[1]==old[k]){//??????????????????????????£??????
							
							space = k-j;
							if(old.length<=space*(target.length-1)+j) continue;//??????????????\???????????¨????????£??????
							work = k;
							
							for(int l=2; l<target.length; l++){
								work+=space;
								if(old[work]!=target[l]) break;//??????????????\???????????£??????
								if(l==target.length-1){
									cnt++;
									flag = false;//???????????£???????????§?¬?????????????
								}
								
							}
						}
					}
				}
				if(flag==false) break;
			}
		}
		
		System.out.println(cnt);
		
	}

}