import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int w,h,i,j,c=2;(w=s.nextInt()+2)>2;System.out.println(c-2),c=2){int[]a=new int[w*(h=s.nextInt()+2)];for(i=1;i<h-1;++i)for(j=1;j<w-1;++j)a[i*w+j]=s.nextInt();for(i=1;i<h-1;++i)for(j=1;j<w-1;++j)if(a[i*w+j]==1)f(a,w,i*w+j,c++);}}static void f(int[]a,int w,int x,int c){if(a[x]==1){a[x]=c;for(int i:new int[]{-w-1,-w,-w+1,-1,1,w-1,w,w+1})f(a,w,x+i,c);}}}