import java.util.Scanner;public class Main {	public static void main(String[] args) {		int n, k;		Scanner sc = new Scanner(System.in);		n = sc.nextInt();		k = sc.nextInt();		int[] T = new int[n];		for (int i = 0; i < n; i++) {			T[i] = sc.nextInt();		}		int P = slove(T, n, k);		System.out.println(P);	}	private static int slove(int[] T, int n, int k) {		int left = 0;		int right = 100000 * 10000;		int k1 = k;		int[] T1 = T;		int n1 = n;		while (right - left > 1) {			int mid = (left + right) / 2;			int v = check(mid, T1, k1, n1);			if (v >= n) 				right = mid;			else 				left = mid;		}		return right;	}	private static int check(int mid, int[] T1, int k1, int n1) {		int i = 0;		for (int j = 0; j < k1; j++) {			int S = 0;			while (S + T1[i] <= mid) {				S += T1[i];				i++;				if (i == n1)					return n1;			}		}		return i;	}}
