class ArrayUtils {
      
	 /**
	  * ???????????????????´????????????§??????????????§?????????????????????????????????????????????????????????????????????
	   *
	   * @param array    ????´¢????????????
	    * @param obj       ????´¢??????????´?
	   * @return ???????????????????´???????????????§??????????????????????????????????????????????????????
	    *               ??????????´?????????????????????´?????? -1
	    */
 public static int indexOf(Object[] array, Object obj) {
     return indexOf(array, obj, 0);
  }
  
 /**
  * ???????????????????´???????int??????????????§?????????????????????????????????????????????????????????????????????
  *
   * @param array    ????´¢????????????
    * @param val       ????´¢??????????´?
   * @return ???????????????????´???????????????§??????????????????????????????????????????????????????
    *               ??????????´?????????????????????´?????? -1
    */
 public static int indexOf(int[] array, int val) {
       return indexOf(array, val, 0);             //?????????????????????????????????????????????????????????????´¢??????
  }
  
 /**
  * ???????????????????´????????????§??????????????§????´¢????§?????????\??????
     * ?????????????????????????????????????????????????????????????????????
   *
   * @param array    ????´¢????????????
    * @param obj       ????´¢??????????´?
   * @param start    ????´¢????§???????
    * @return ???????????????????´???????????????§??????????????????????????????????????????????????????
    *               ??????????´?????????????????????´?????? -1
    */
 public static int indexOf(Object[] array, Object obj, int start) {
      if (array.length == 0) {
            return -1;
      }
       if (obj != null) {
          for (int i = start; i < array.length; i++) {
             if (obj.equals(array[i])) return i;
         }
       } else {
            for (int i = start; i < array.length; i++) {
             if (array[i] == null) return i;
         }
       }
       return -1;
  }
  
 /**
  * ???????????????????´???????int??????????????§????´¢????§?????????\??????
    * ?????????????????????????????????????????????????????????????????????
   *
   * @param array    ????´¢????????????
    * @param val       ????´¢??????????´?
   * @param start    ????´¢????§???????
    * @return ???????????????????´???????????????§??????????????????????????????????????????????????????
    *               ??????????´?????????????????????´?????? -1
    */
 public static int indexOf(int[] array, int val, int start) {
        if (array.length == 0) {             //??????????????????0?????¨???
            return -1;                       //-1?????????
        }
     for (int i = start; i < array.length; i++) {      //????????????????????§??°?????????
         if (val == array[i]) return i;                //????????????????????¨??????????????????????????????????´???°?????????
      }
       return -1;                                      //?????????????????????????´??????????????????????????????£?????´???
  }                                                    //-1?????????
}