class Main {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner as = new Scanner(System.in);
		
		String T;
		String TD;
		String W;
		String str;
		int j = 0;
		int i = 0;
		int count = 0;
		int same= 0;
	    char Tli[];
	    char Wli[];
	    
	    TD = "";
	    
	    W = as.nextLine();
	    
	    
	    
	    while(as.hasNext()){                  //EOF?????§??±????????????????????£?????????
            
            TD = TD + as.nextLine(); 
             
        }
	    
	    Tli = new char[TD.length()];
	    Wli = new char[W.length()];
	    
	    TD = TD.toLowerCase();
	    W = W.toLowerCase();
	    
	    for(i=0;i<TD.length();i++){      //?????????????????????????????????????´???????
            
            Tli[i] = TD.charAt(i);
             
        }
	    
		for(i=0;i<W.length();i++){      //?????????????????????????????????????´???????
		    
			Wli[i] = W.charAt(i);
		     
		}
		
		for(i=0;i<(TD.length()-W.length());i++){      //?????????????????????????????????????´???????
		    
			for(j=0;j<W.length();j++){
			
				if(Tli[j+i] == Wli[j]){
		        	
					count++;
					
				}
				else{
					
					count = 0;
				
				}
				
				
			}
			
			
			if(count == W.length()){
				
				same++;
				
			}
			
			count = 0;
			
			
        }
		
		str = String.valueOf(same);
		System.out.println(str);

	}

}