import java.util.*;public class Main{public static void main(String[] args){Scanner c=new Scanner(System.in);int n=c.nextInt();String s=c.next();long[] q=new long[3];for(int i=0;i<n;i++){if(s.charAt(i)=='R')q[0]++;else if(s.charAt(i)=='G')q[1]++;else q[2]++;}long y=q[0]*q[1]*q[2];for(int i=0;i<n-2;i++){for(int j=i+1;j<n-1;j++){if(2*j-i<n&&s.charAt(i)!=s.charAt(j)&&s.charAt(j)!=s.charAt(2*j-i)&&s.charAt(2*j-i)!=s.charAt(i))y--;}}System.out.println(y);}}