import java.util.*;class Main{public static void main(String[]r){Scanner sc=new Scanner(System.in);int i,u,n=sc.nextInt(),m=sc.nextInt(),x=sc.nextInt(),w=Integer.MAX_VALUE,q=Integer.MAX_VALUE;int[]a=new int[n],b=new int[m];for(i=0;i<n;i++)w=(w<(a[i]=sc.nextInt()))?w:a[i];for(i=0;i<m;i++)q=(q<(b[i]=sc.nextInt()))?q:b[i];u=q+w;for(i=0;i<x;i++)u=(u<(q=a[sc.nextInt()-1]+b[sc.nextInt()-1]-sc.nextInt()))?u:q;System.out.println(u);}}