import java.util.*;class Main{public static void main(String[]r){Scanner sc=new Scanner(System.in);int i,n=sc.nextInt(),m=sc.nextInt(),x=sc.nextInt(),w=9999,q=w;int[]a=new int[n],b=new int[m];for(i=0;i<n;i++)w=(w<(a[i]=sc.nextInt()))?w:a[i];for(i=0;i<m;i++)q=(q<(b[i]=sc.nextInt()))?q:b[i];n=q+w;for(i=0;i<x;i++)n=(n<(q=a[sc.nextInt()-1]+b[sc.nextInt()-1]-sc.nextInt()))?n:q;System.out.println(n);}}