import java.util.*;
public class atcoder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		String s = sc.next();
		if(s.equals("A")) {
			System.out.println("T");
		}else if(s.equals("T")) {
			System.out.println("A");
		}else if(s.equals("C")) {
			System.out.println("G");
		}else if(s.equals("G")) {
			System.out.println("C");
		}
	}

}
