import java.io.*;
import java.util.*;

class Main {

    // Generated by 1.1.7.1 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
    public static void main(String[] args) throws Exception {
        final Scanner sc = new Scanner(System.in);
        long N;
        N = sc.nextLong();
        long[] a = new long[(int)(N)];
        long[] b = new long[(int)(N)];
        long[] c = new long[(int)(N)];
        for(int i = 0 ; i < N ; i++){
                a[i] = sc.nextLong();
                b[i] = sc.nextLong();
                c[i] = sc.nextLong();
        }
        solve(N, a, b, c);
    }

    static void solve(long N, long[] a, long[] b, long[] c){
        long[][] dp = new long[(int)(N+1)][3];

        for (int i = 1; i <= N; i++){
            dp[i][0] = Math.max(dp[i-1][1] + a[i-1], dp[i-1][2] + a[i-1]);
            dp[i][1] = Math.max(dp[i-1][2] + b[i-1], dp[i-1][0] + b[i-1]);
            dp[i][2] = Math.max(dp[i-1][0] + c[i-1], dp[i-1][1] + c[i-1]);
        }

        long rst = 0;
        rst = Math.max(dp[(int)(N)][0], dp[(int)(N)][1]);
        rst = Math.max(rst, dp[(int)N][2]);
        System.out.print(rst);
        
    }
}
