import java.util.*;import static java.lang.Math.*;class Main{public static void main(String[]z){new Main();}Main(){Scanner s=new Scanner(System.in);for(int t=s.nextInt(),i,x,y;t-->0;){double[][]p=new double[3][6];for(i=3;i-->0;p[1][i]=s.nextDouble())p[0][i]=s.nextDouble();for(i=6;i-->0;y=(i+2)%3,p[2][i]=sqrt(pow(p[0][x]-p[0][y],2)+pow(p[1][x]-p[1][y],2)))x=(i+1)%3;System.out.printf("%.3f %.3f %.3f\n",l(p[0],p[2]),l(p[1],p[2]),p[2][0]/(2*sqrt(1-pow(c(p[2],0),2))));}}double l(double[]p,double[]l){double r=0,t=0,s;for(int i=0;i<3;r+=p[i++]*s,t+=s)s=a(l,i);return r/t;}double a(double[]l,int i){return 2*c(l,i)*sqrt(1-pow(c(l,i),2));}double c(double[]l,int i){return(pow(l[i+1],2)+pow(l[i+2],2)-pow(l[i],2))/(2*l[i+1]*l[i+2]);}}