import java.util.*;class Main{public static void main(String[]z){Scanner s = new Scanner(System.in);for(int t=s.nextInt(),i,j;t-->0;){double[][]p=new double[3][2];for(i=0;i<3;++i)for(j=0;j<2;++j)p[i][j]=s.nextDouble();double[]b={p[1][1]-p[0][1],p[2][1],p[1][1]},a={p[1][0]-p[0][0],p[2][0]-p[1][1]},c={-a[0]*(p[0][0]+p[1][0])/2,-a[1]*(p[1][0]+p[2][0])/2},r={(b[0]*c[1]-b[1]*c[0])/(a[0]*b[1]-a[1]*b[0]),(a[1]*c[0]-a[0]*c[1])/(a[0]*b[1]-a[1]*b[0])};System.out.printf("%.3f %.3f %.3f\n",r[0],r[1],Math.sqrt(Math.pow(r[0]-p[0][0],2)+Math.pow(r[1]-p[1][0],2)));}}}