import java.util.*;import java.awt.geom.*;class Main{public static void main(String[]_){Scanner S=new Scanner(System.in);int i,f;double a[]=new double[8];for(Path2D P=new Path2D.Double();S.hasNext();System.out.println(f<1&&P.contains(a[6],a[7])?"YES":"NO")){for(i=0;i<8;P.reset())a[i++]=S.nextDouble();for(f=i=0;i<6;i+=2)if(a[i]==a[6]&&a[i+1]==a[7])f=1;P.moveTo(a[0],a[1]);for(i=2;i<8;i+=2)P.lineTo(a[i%6],a[(i+1)%6]);}}}