import java.util.*;import java.awt.geom.*;class Main{public static void main(String[]_){Scanner S=new Scanner(System.in);int i,f;double p,x,y,u,v,a[]=new double[8];for(Path2D P=new Path2D.Double();S.hasNext();System.out.println(f<1&&P.contains(a[6],a[7])?"YES":"NO")){for(i=0;i<8;P.reset())a[i++]=S.nextDouble();for(f=i=0;i<6;i+=2){x=a[(i+2)%6]-a[i];y=a[(i+3)%6]-a[i+1];u=a[i]-a[6];v=a[i+1]-a[7];p=x*u+y*v;if(p<=Math.hypot(x,y)&&p>=0.0)f=1;}P.moveTo(a[0],a[1]);for(i=2;i<8;i+=2)P.lineTo(a[i%6],a[(i+1)%6]);}}}