import java.io.*;class Main{public static void main(String[]g)throws Exception{BufferedReader B=new BufferedReader(new InputStreamReader(System.in));int i,j;String[]s=new String[26];for(i=0;i<26;)s[i++]="";char[]c=B.readLine().toCharArray();for(i=0;i<c.length;i++)for(j=0;j<26;j++)if(c[i]>96&&c[i]<123)s[j]+=(char)((c[i]-97+j)%26+97);else s[j]+=c[i];for(i=0;i<26;i++)if(s[i].matches(".* th(e|at|is) .*"))break;System.out.println(s[i]);}}
