import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);String x;int i,n;Map<Integer,Set<String>>m=new TreeMap<Integer,Set<String>>();for(;!(x=s.next()).equals("0,0");){z=x.split(",");if(!m.containsKey(n=new Integer(z[1])))m.put(n,new HashSet<String>());m.get(n).add(z[0]);}for(;s.hasNext();System.out.println(i)){x=s.next();i=m.size();for(int k:m.keySet()){if(m.get(k).contains(x))break;--i;}}}}