import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int i,j,c=0;s.hasNext();System.out.println(c),c=0){int[]a=new int[156];for(i=0;i<12;++i){z=s.next().split("");for(j=0;j<12;++j)a[i*13+j]=z[j+1].charAt(0);}for(i=0;i<12;++i)for(j=0;j<12;++j)if(a[i*13+j]>48)f(a,i*13+j,c++);}}static void f(int[]a,int x,int c){if(0<=x&&x<156&&a[x]>48){a[x]=(byte)-c;for(int i:new int[]{-13,-1,1,13})f(a,x+i,c);}}}