import java.util.*;public class Main{public static void main(String[] a){Scanner s=new Scanner(System.in);for(;;){int h=s.nextInt()+1;int w=s.nextInt()+1;if(h*w==1)break;int[][]l=new int[h][w];int[][]t=new int[h][w];int m=0;for(int i=1;i<h;i++){char[]c=("*"+s.next()).toCharArray();for(int j=1;j<w;j++){if(c[j]!='*')l[i][j]=l[i][j-1]+1;t[i][j]=l[i][j];for(int k=0;k<i;k++)t[k][j]=Math.min(t[k][j],l[i][j]);for(int k=0;k<i;k++)m=Math.max(m,t[k][j]*(i-k+1));}}System.out.println(m);}}}