public class Main {
	// ??\??????
	static int w, h;
	static String field[][];

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int res = 0;
		//
		for (int i = 0; i < w * h; i++) {
			String target = getTarget();
			//"."??\??????????????????????????£?????????????????????
			if (target == null) {
				System.out.println(res);
			}
			// ?????????????????¨?????¢?????????????????????
			for (int x = 0; x < w; x++) {
				for (int y = 0; y < h; y++) {
					if (field[x][y].equals(target)) {
						dfs(x, y);
						res++;
					}
				}
			}
		}
	}

	// "."??????????????°?????????????????¢???
	static String getTarget() {
		for (int x = 0; x < w; x++) {
			for (int y = 0; y < h; y++) {
				if (field[x][y] != ".") {
					return field[x][y];
				}
			}
		}
		return null;
	}

	// ?????¨??????x,y ?°´????????????????????¨????????§?????°????????¢??°
	static void dfs(int x, int y) {
		// ???????????¨?????????.?????????
		field[x][y] = ".";
		// 8?????????*????????????
		for (int dx = x - 1; dx < x + 1; dx++) {
			for (int dy = y - 1; dy < y + 1; dy++) {
				if (field[dx][dy] == "W") {
					field[dx][dy] = ".";
				}
			}
		}
	}

}