import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int h,w,i,j,c,u,v,o;(h=s.nextInt())>0;System.out.println(c)){char[][]x=new char[h][w=s.nextInt()];for(i=0;i<h;++i)x[i]=s.next().toCharArray();for(c=i=0;i<h;++i)for(j=0;j<w;++j)if(x[i][j]>0){Queue<int[]>l=new LinkedList<int[]>();for(l.add(new int[]{i,j,x[i][j]});!l.isEmpty();){int[]k=l.poll();u=k[0];v=k[1];o=k[2];if(0<=u&&u<h&&0<=v&&v<w&&x[u][v]==o){x[u][v]=0;for(int[]e:new int[][]{{u+1,v,o},{u-1,v,o},{u,v+1,o},{u,v-1,o}})l.add(e);}}++c;}}}}