import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int h,w,i,j,c;(h=s.nextInt())>0;System.out.println(c-1)){w=s.nextInt();char[][]x=new char[h][w];for(i=0;i<h;++i)x[i]=s.next().toCharArray();for(c=1,i=0;i<h;i++)for(j=0;j<w;j++)if(x[i][j]>0)f(x,i,j,c++,x[i][j]);}}static void f(char[][]x,int i,int j,int c,char y){if(i<0||x.length<=i||j<0||x[i].length<=j||x[i][j]!=y)return;x[i][j]=0;f(x,i+1,j,c,y);f(x,i-1,j,c,y);f(x,i,j+1,c,y);f(x,i,j-1,c,y);}}