import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int h,w,i,j,c;(h=s.nextInt())>0;System.out.println(c)){char[][]x=new char[h][w=s.nextInt()];for(i=0;i<h;++i){String y=s.next();for(j=0;j<w;++j)x[i][j]=y.charAt(j);}for(c=i=0;i<h;i++)for(j=0;j<w;j++)if(x[i][j]>0){f(x,i,j,x[i][j]);++c;}}}static void f(char[][]x,int i,int j,char y){if(0<=i&&i<x.length&&0<=j&&j<x[0].length&&x[i][j]==y){x[i][j]=0;f(x,i+1,j,y);f(x,i-1,j,y);f(x,i,j+1,y);f(x,i,j-1,y);}}}