import java.util.*;class Main{public static void main(String[]z){Scanner s = new Scanner(System.in);String x="";for(int n,i;(n=s.nextInt())>0;x+="\n"){Map<String,Integer>m=new HashMap<String,Integer>();for(i=n;i-->0;)m.put(s.next(),s.nextInt()*3+s.nextInt()*0+s.nextInt());List<Integer>c=new ArrayList<Integer>(m.values());Set<String>e=new HashSet<String>();Collections.sort(c);for(;n-->0;)for(String k:m.keySet())if(c.get(n).equals(m.get(k))&&e.add(k))x+=k+","+c.get(n)+"\n";}System.out.print(x);}}