import java.awt.*;import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int n,i,j,t;(n=s.nextInt())>0;){double[][]a=new double[++n][n];int[][]b=new int[n][n];Point[]p=new Point[n];for(i=1;i<n;++i){p[s.nextInt()]=new Point(s.nextInt(),s.nextInt());Arrays.fill(a[i],Float.MAX_VALUE);a[i][i]=0;}for(i=1;i<n;++i)for(j=1;j<n;++j)if(p[i].distance(p[j])<=50){a[i][j]=p[i].distance(p[j]);b[i][j]=i;}for(t=1;t<n;++t)for(i=1;i<n;++i)for(j=1;j<n;++j)if(a[i][j]>a[i][t]+a[t][j]){a[i][j]=a[i][t]+a[t][j];b[i][j]=b[t][j];}for(t=s.nextInt();t-->0;){String r="";i=s.nextInt();j=s.nextInt();for(r+=j;i!=j;r=j+" "+r){j=b[i][j];if(j<1){r="NA";break;}}System.out.println(r);}}}}