import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int n,m,r;(n=s.nextInt())>0;System.out.println(r)){List<int[]>x=new ArrayList<int[]>();for(r=0,m=s.nextInt();m-->0;)x.add(new int[]{s.nextInt(),s.nextInt(),s.nextInt()});Collections.sort(x,new Comparator<int[]>(){public int compare(int[]a,int[]b){return a[2]-b[2];}});U u=new U(n);for(int[]e:x)if(!u.s(e[0],e[1])){u.u(e[0],e[1]);r+=e[2];}}}static class U{int[] p,r;U(int n){p=new int[n];r=new int[n];for(int i=0;i<n;++i)p[i]=i;}int f(int x){return(p[x]==x)?x:(p[x]=f(p[x]));}void u(int x,int y){x=f(x);y=f(y);if(x==y)return;if(r[x]<r[y]){p[x]=y;}else{p[y]=x;if(r[x]==r[y])++r[x];}}boolean s(int x,int y){return f(x)==f(y);}}}