import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);for(int n,t,i,j;(n=s.nextInt()+1)>1;){long[][]a=new long[n][n];for(i=0;i<n;a[i][i++]=0)Arrays.fill(a[i],n*n);for(t=s.nextInt();t-->0;)a[i=s.nextInt()][j=s.nextInt()]=a[j][i]=1;for(t=1;t<n;++t)for(i=1;i<n;++i)for(j=1;j<n;++j)if(a[i][j]>a[i][t]+a[t][j])a[i][j]=a[i][t]+a[t][j];t=0;for(long m:a[1])if(m<3)++t;System.out.println(t-1);}}}