import java.util.*;class Main{public static void main(String[]z){Scanner s=new Scanner(System.in);char[][]a;for(int w,h,x,y,i,j;(w=s.nextInt()+2)>2;System.out.println(f(a,x,y))){a=new char[h=s.nextInt()+2][w];for(i=x=y=1;i<h-1;++i){j=0;for(char c:s.next().toCharArray()){a[i][++j]=c=='#'?0:c;if(a[i][j]>63){x=i;y=j;}}}}}static int f(char[][]a,int x,int y){if(a[x][y]<1)return 0;a[x][y]=0;return f(a,x-1,y)+f(a,x+1,y)+f(a,x,y-1)+f(a,x,y+1)+1;}}