import java.util.*;class Main{public static void main(String[]v){Scanner s=new Scanner(System.in);for(int n,i,j,t,r=Integer.MAX_VALUE;(n=s.nextInt())>0;){int m=s.nextInt(),l=s.nextInt(),k=s.nextInt(),a=s.nextInt(),h=s.nextInt();long[][]p=new long[n][n];long e;for(i=n;i-->0;)Arrays.fill(p[i],r);int[]b=new int[n];for(b[a]=b[h]=1;l-->0;)b[s.nextInt()]=1;for(;k-->0;){i=s.nextInt();j=s.nextInt();t=s.nextInt();p[i][j]=p[j][i]=t;}f(n,p);for(i=n;i-->0;)for(j=n;j-->0;)if(p[i][j]>m||b[i]+b[j]<2)p[i][j]=r;f(n,p);System.out.println((e=p[a][h])<r?e*2-m:"Help!");}}static void f(int n,long[][]p){int t=n,i,j;for(;t-->0;)for(i=n;i-->0;)for(j=n;j-->0;){long e=p[i][t]+p[t][j];if(p[i][j]>e)p[i][j]=e;}}}