    private long work(int s,int k) {
        if (dp[s][k] != 0) {
            return dp[s][k];
        }
        if (s == k) {
            return dp[s][k] = 1;
        }
        if (k == 1) {
            return dp[s][k] = 1;
        }
        long ans = 0;
        for (int i = k-1; i <= s-1; i++) {
            ans = (ans + work(i,k-1))%MOD;
        }
        return dp[s][k] = ans;
    }

