import java.util.*;class Main{public static void main(String[]r){Scanner c=new Scanner(System.in);int i,n=c.nextInt(),m=c.nextInt(),x=c.nextInt(),w=999999,q=w;int[]a=new int[n],b=new int[m];for(i=0;i<n;i++)w=(w<(a[i]=c.nextInt()))?w:a[i];for(i=0;i<m;i++)q=(q<(b[i]=c.nextInt()))?q:b[i];n=q+w;for(i=0;i<x;i++)n=(n<(q=a[c.nextInt()-1]+b[c.nextInt()-1]-c.nextInt()))?n:q;System.out.print(n);}}