public class Main{public static void main(String[] args) {java.util.Scanner s=new java.util.Scanner(System.in);int n=s.nextInt();long k=s.nextLong();long[] a=new long[n];for(int i=0;i<n;i++)a[i]=s.nextLong();int c=0;while((long)1<<(c+1)<= k)c++;long x=0;for(;c>=0;c--){long m=x+((long)1<<c);if(m<=k){long t=0,u=0;for(long l:a){t+=m^l;u+=x^l;}if(t>=u)x=m;}}long r=0;for(long i:a)r+=x^i;System.out.println(r);}}