import java.io.*;
import java.util.*;

class Main {
    static final long MOD = 1000000007;

    // Generated by 1.1.7.1 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
    public static void main(String[] args) throws Exception {
        final Scanner sc = new Scanner(System.in);
        long H;
        H = sc.nextLong();
        long W;
        W = sc.nextLong();
        char[][] a = new char[(int)(H)][(int)(W)];
        for(int i = 0 ; i < H ; i++){                
            a[i] = sc.next().toCharArray();               
        }
        solve(H, W, a);
    }

    static void solve(long H, long W, char[][] a){
        
        long[][] dp = new long[(int)(H+1)][(int)(W+1)];

        for (int i = 1; i <= (int)(H); i++){
            if (a[i-1][0] == '.'){
                dp[i][1] = 1;
            }else{
                break;
            }
        }

        for (int i = 1; i <= (int)(W); i++){
            if (a[0][i-1] == '.'){
                dp[1][i] = 1;
            }else{
                break;
            }
        }

        for (int i = 2; i <= (int)H; i++){
            for (int j = 2; j <= (int)W; j++){
                long up = a[i-2][j-1] == '.' ? dp[i-1][j] : 0;
                long left = a[i-1][j-2] == '.' ? dp[i][j-1] : 0;
                dp[i][j] = a[i-1][j-1] == '.' ? (up + left) % MOD : 0;
            }
        } 

        System.out.print(dp[(int)H][(int)W]);
    }
}
