import java.io.*;
import java.util.*;

class Main {

    // Generated by 1.1.6 https://github.com/kyuridenamida/atcoder-tools  (tips: You use the default template now. You can remove this line by using your custom template)
    public static void main(String[] args) throws Exception {
        final Scanner sc = new Scanner(System.in);
        String s;
        s = sc.next();
        solve(s);
    }

    static void solve(String s){
        String[] str = s.split("");
        int n = str.length;
        int k[] = new int[(int)n+1];
        for(int i = 1 ; i <= n ; i++) k[i] = Integer.parseInt(str[i-1]);
        if( k[1] == 0 || k[n] == 1) exit();
        for(int i = 1 ; i < (n+1)/2 ; i++){
            if(k[i] != k[n-i]) exit();
        }
        int v[] = new int[(int)n];
        int next_one = 1;
        for(int i = 1 ; i < n ; i++){
            if(k[i] == 1){
                for(int j = i+1 ; j < n ; j++){
                    if(k[j] == 1){
                        v[i] = j;
                        next_one = j;
                        break;
                    }
                }
            }else{
                v[i] = next_one;
            }
            v[n-1] = n;
            System.out.println(i + " " + v[i]);
        }
    }

    static void exit(){
        System.out.println(-1);
        System.exit(0);
    }
}
